const processQuestions = (questions: any[]): Question[] => {
  return questions.map(q => {
    const newQ: Question = { ...q };
    // If flags are already explicitly set, respect them and continue
    if (newQ.isMatching === true || newQ.isMultipleChoice === true) {
        return newQ;
    }

    // Otherwise, infer from the answer type
    if (Array.isArray(newQ.answer)) {
      newQ.isMultipleChoice = true;
    } else if (typeof newQ.answer === 'object' && newQ.answer !== null && !Array.isArray(newQ.answer)) {
      if(Object.keys(newQ.answer).length > 0) {
        newQ.isMatching = true;
      }
    }
    return newQ;
  });
};

